import React,{useState} from 'react'
import styled from 'styled-components'
import MenuIcon from '@material-ui/icons/Menu';
import CloseIcon from '@material-ui/icons/Close';
function Header() {

    const [burgerStatus, setBurgerStatus] = useState(false);

    return (
        <Container>
            <a>
                <img  src="/images/logo.svg" alt="" />
            </a>

            <Menu>
<p><a href="#">model S</a></p>
<p><a href="#">model 3</a></p>
<p><a href="#">model X</a></p>
<p><a href="#">model Y</a></p>
<p><a href="#">Solar roof</a></p>
<p><a href="#">solar panels</a></p>


            </Menu>
<RightMenu>
    <a href="#" >shop</a>
    <a href="#" >Tesla Account</a>
    <CustomMenu onClick={() => setBurgerStatus(true)} />

</RightMenu>

<BurgerNav show={burgerStatus}>
<CloseWrapper>
<CustomClose/>
</CloseWrapper>

<li><a href="#">class s</a></li>
<li><a href="#">class 3</a></li>
<li><a href="#">class y</a></li>
<li><a href="#">class x</a></li>
<li><a href="#">Existing Inventory</a></li>
<li><a href="#">Existing Inventory</a></li>
<li><a href="#">Existing Inventory</a></li>
<li><a href="#">Existing Inventory</a></li>
<li><a href="#">Existing Inventory</a></li>



</BurgerNav>


        </Container>
    )
}

export default Header


const Container = styled.div `
min-height:60px;
position:fixed;
display:flex;
align-items:center;
padding:0 20px;
Top:0;
Left:0;
Right:0;
z-index: 1;

`


const Menu = styled.div `
display: flex;
align-items:center;
justify-content:center;
flex:1;

a{
     font-family: 'Gotham Medium',-apple-system,BlinkMacSystemFont,"Segoe UI",Roboto,"Helvetica Neue",Arial,sans-serif; 
    font-weight:bold;
    font-size:14px;
    color:#000;
    text-transform:uppercase;
    padding:0 10px;
    flex-Wrap:normal;

}


`
const RightMenu = styled.div `
display:flex;
align-items:center;
a{
    font-family: 'Gotham Medium',-apple-system,BlinkMacSystemFont,"Segoe UI",Roboto,"Helvetica Neue",Arial,sans-serif; 
    font-weight:600;
    font-size:14px;
    color:#000;
    text-transform:uppercase;
    padding:0 10px;
    flex-Wrap:normal;
    margin-right:10px;

}

`
const CustomMenu = styled(MenuIcon) `
cursor:pointer;

`


const BurgerNav = styled.div `
position:fixed;
top:0;
bottom:0;
right:0;
background:white;
width:300px;
z-index:16;
list-style:none;
padding:20px;
display:flex;
flex-direction:column;
text-align:start;
transform: ${props => props.show ? 'transulate X(0)' :'transulate X(100)' };
li {
    padding:15px 0;
    border-bottom:1px solid rgba(0, 0, .2);

    a{
        font-weight:600;
    }
}

`
const CustomClose = styled (CloseIcon) `
cursor:pointer;

`

const CloseWrapper = styled.div `
display:flex;
justify-content:flex-end;
`
